function l(a,e,m,c,r,t){return new Promise((i,B)=>{const s=16.666666666666668,v=Math.round(m/s),d=Math.abs(e-a)/v;let n=a;const x=setInterval(function(){n+=d,c[r]=t?n+t:n,n>=e&&(c[r]=t?e+t:e,clearInterval(x),i())},s)})}const o=document.getElementById("box"),f=document.getElementById("moveBoxBtn");f.addEventListener("click",()=>{l(0,200,1200,o.style,"left","px").then(()=>l(0,200,1200,o.style,"top","px")).then(()=>o.innerText="Moving is done!")});
