async function l(e,t,i,c,s,n){return new Promise((m,f)=>{const r=16.666666666666668,d=Math.round(i/r),v=Math.abs(t-e)/d;let o=e;const x=setInterval(function(){o+=v,c[s]=n?o+n:o,o>=t&&(c[s]=n?t+n:t,clearInterval(x),m(t))},r)})}const a=document.getElementById("box"),B=document.getElementById("moveBoxBtn");B.addEventListener("click",async()=>{const e=await l(0,200,1200,a.style,"left","px"),t=await l(0,600,1200,a.style,"top","px");a.innerText=`Moving to ${e} and ${t} is done!`});
