function f(o,e,l,c,a,t){return new Promise((m,B)=>{const r=16.666666666666668,i=Math.round(l/r),v=Math.abs(e-o)/i;let n=o;const d=setInterval(function(){n+=v,c[a]=t?n+t:n,n>=e&&(c[a]=t?e+t:e,clearInterval(d),m())},r)})}const s=document.getElementById("box"),x=document.getElementById("moveBoxBtn");x.addEventListener("click",function(){f(0,200,1200,s.style,"left","px").then(()=>s.innerText="Moving is done!")});
